package com.example.store_online.adapter;import android.app.Activity;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import androidx.viewpager2.widget.ViewPager2;import com.bumptech.glide.Glide;import com.example.store_online.R;import com.example.store_online.data_models.PhotoBanner;import java.util.ArrayList;import java.util.List;public class PhotoBannerAdapter extends RecyclerView.Adapter<PhotoBannerAdapter.PhotoHolder> {    private Activity activity;    private int layoutID;    private List<PhotoBanner> listBanner;    public PhotoBannerAdapter(Activity activity, int layoutID, List<PhotoBanner> listBanner) {        this.activity = activity;        this.layoutID = layoutID;        this.listBanner = listBanner;    }    @NonNull    @Override    public PhotoHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater inflater = activity.getLayoutInflater();        View view = inflater.inflate(viewType,parent,false);        return new PhotoHolder(view);    }    @Override    public void onBindViewHolder(@NonNull PhotoHolder holder, int position) {        PhotoBanner photoBanner = listBanner.get(position);        Glide.with(activity)                .load(photoBanner.getImage())                .error(R.drawable.ic_baseline_account_circle_24)                .into(holder.imgBannner);    }    @Override    public int getItemCount() {        return listBanner.size();    }    public int getLayoutID() {        return layoutID;    }    public class PhotoHolder extends RecyclerView.ViewHolder{        private ImageView imgBannner;        public PhotoHolder(@NonNull View itemView) {            super(itemView);            imgBannner = itemView.findViewById(R.id.img_banner);        }    }}