package com.example.store_online.adapter;import androidx.annotation.NonNull;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentActivity;import androidx.viewpager2.adapter.FragmentStateAdapter;import com.example.store_online.fragment.AccountFragment;import com.example.store_online.fragment.CategoryFragment;import com.example.store_online.fragment.HomePageFragment;public class ViewPagerAdapter extends FragmentStateAdapter {    public ViewPagerAdapter(@NonNull FragmentActivity fragment) {        super(fragment);    }    @NonNull    @Override    public Fragment createFragment(int position) {        switch (position) {            case 0:                return new HomePageFragment();            case 1:                return new CategoryFragment();            case 2:                return new AccountFragment();            default:                return new HomePageFragment();        }    }    @Override    public int getItemCount() {        return 3;    }}