package com.example.store_online.dialog;import android.app.Activity;import android.view.LayoutInflater;import android.view.View;import android.widget.Button;import android.widget.TextView;import androidx.appcompat.app.AlertDialog;import androidx.constraintlayout.widget.ConstraintLayout;import com.example.store_online.R;public class ErrorDialog {    private Activity activity;    private AlertDialog dialog;    private  View view;    public ErrorDialog(Activity myActivity) {        activity=myActivity;    }    public void startErrorDialog(String message) {        AlertDialog.Builder builder = new AlertDialog.Builder(activity);        view = LayoutInflater.from(activity).inflate(                R.layout.dialog_error,null);        builder.setView(view);        TextView txtMessage =view.findViewById(R.id.txtMessage_dialog);        builder.setCancelable(false);        txtMessage.setText(message);        dialog = builder.create();        dialog.show();        Button btnError= view.findViewById(R.id.btn_error_dialog);        btnError.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                dialog.dismiss();            }        });    }}