package com.example.store_online.dialog;import android.app.Activity;import android.view.LayoutInflater;import android.view.View;import android.widget.Button;import android.widget.TextView;import androidx.appcompat.app.AlertDialog;import com.example.store_online.R;public class ErrorDialog {    private Activity activity;    private AlertDialog dialog;    public ErrorDialog(Activity myActivity) {        activity=myActivity;    }    public void startErrorDialog(String message) {        TextView txtMessage =dialog.findViewById(R.id.txtTitle_dialog);        AlertDialog.Builder builder = new AlertDialog.Builder(activity);        LayoutInflater inflater = activity.getLayoutInflater();        builder.setView(inflater.inflate(R.layout.dialog_error, null));        builder.setCancelable(false);        txtMessage.setText(message);        dialog = builder.create();        dialog.show();    }    public void endErrorDialog() {        Button btnError= dialog.findViewById(R.id.btn_error_dialog);        btnError.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                dialog.dismiss();            }        });    }}