package com.example.store_online.dialog;import android.app.Activity;import android.graphics.drawable.ColorDrawable;import android.view.LayoutInflater;import androidx.appcompat.app.AlertDialog;import com.example.store_online.R;public class LoadingDialog {    private Activity activity;    private AlertDialog dialog;    public LoadingDialog(Activity myActivity) {        activity = myActivity;    }    public void startLoadingDialog() {        AlertDialog.Builder builder = new AlertDialog.Builder(activity);        LayoutInflater inflater = activity.getLayoutInflater();        builder.setView(inflater.inflate(R.layout.dialog_loading, null));        builder.setCancelable(false);        dialog = builder.create();        if (dialog.getWindow() != null) {            dialog.getWindow().setBackgroundDrawable(new ColorDrawable(0));        }        dialog.show();    }    public void endLoadingDialog() {        dialog.dismiss();    }}