package com.example.store_online.dialog;import android.app.Activity;import android.view.LayoutInflater;import androidx.appcompat.app.AlertDialog;import com.example.store_online.R;public class LoadingDialog {    private Activity activity;    private AlertDialog dialog;    LoadingDialog(Activity myActivity) {        myActivity = activity;    }    void starLoadingDialog() {        AlertDialog.Builder builder = new AlertDialog.Builder(activity);        LayoutInflater inflater = activity.getLayoutInflater();        builder.setView(inflater.inflate(R.layout.dialog_loading, null));        builder.setCancelable(true);        dialog = builder.create();        dialog.show();    }    void endLoadingDialog() {        dialog.dismiss();    }}